def isinarea(aPoint, aBorder):
    """ This checks if aPoint is inside the area surrounded by aBorder.
    The border should be a list of Gp-points. The point can be
    either Gp poin or [lon, lat].
    """

    if isinstance(aPoint, Gp):
        alon = aPoint.lon
        alat = aPoint.lat
    else:
        alon = aPoint[0]
        alat = aPoint[1]
    
    aBrdr = aBorder.copy()
    if aBrdr[-1] != aBrdr[0]:
        aBrdr.append(aBrdr[0])

    lon = [p.lon for p in aBrdr]
    lat = [p.lat for p in aBrdr]
    minlon = min(lon)
    maxlon = max(lon)
    minlat = min(lat)
    maxlat = max(lat)
        
    over = 0
    if minlon <= alon <= maxlon and minlat <= alat <= maxlat:
        # the point is inside the boundingbox, 
        # so have to check if it is inside the polygon
        for i in range(len(aBrdr) - 1):
            milo = min(lon[i], lon[i + 1])
            malo = max(lon[i], lon[i + 1])
            mila = min(lat[i], lat[i + 1])
            mala = max(lat[i], lat[i + 1])
            ix = 0
            if mila <= alat < mala:
                if alon <= milo:
                    ix = 1
                else:
                    if alon <= malo:
                        if lat[i + 1] == lat[i]:
                            ix = 1
                        else:
                            kk = (lon[i + 1] - lon[i])/(lat[i + 1] - lat[i])
                            x = lon[i] + kk*(alat - lat[i])
                            if (x - alon) >= 0.0:
                                ix = 1
            over = over + ix
        
    if over > 0 and over % 2 != 0:
        return True
    else:
        return False

def gsw_Baltic(lon, lat):
    """ Check if the point is inside the Baltic Sea as defined in TEOS-10."""
    if (12.6 <= lon <= 32 and 53 <= lat <= 66):
        return True
    elif 7 < lon < 12.6 and 50 < lat < 59:
        return True         
    else:
        return False
    
def in_which(aPoint, areas, par, def_val):
    """ Check in which area within a area list aPoint is."""
    for a in areas:
        if isinarea(aPoint, a.border):
            for name in a._fields:
                if name == par:
                    return getattr(a, par)
    return ''

def Helcom_areacode(aPoint):
    return in_which(aPoint, BalticSeaAreas, 'helcomcode', '')

def MyCruise_Helcom_areacode(aPoint):
    return in_which(aPoint, BalticSeaAreas, 'mccode', '')

def sea_area_name(aPoint):
    return in_which(aPoint, BalticSeaAreas, 'name', '')

def Helcom_area_and_basin_name(aPoint):
    for area in BalticSeaAreas:
        if isinarea(aPoint, area.border) == True:
            return f"{area.name}, {area.basin}"
    return ''

def getBalticSeaMainAreaName(aPoint):
    return in_which(aPoint, BalticSeaMainAreas, 'basin', '')

def whos_economiczone(aPoint):
    return in_which(aPoint, economiczones, 'name', '')

def whos_territorialWater(aPoint):
    return in_which(aPoint, territorialwaters, 'name', '')

def whos_baseline(aPoint):
    return in_which(aPoint, baselines, 'name', '')

def getMyCruiseCountryCode(aPoint):
    return in_which(aPoint, economiczones, 'mccode', '0')

def gcDistance_nmi(aPoint, bPoint):
    lo1 = radians(aPoint[0])
    la1 = radians(aPoint[1])
    lo2 = radians(bPoint[0])
    la2 = radians(bPoint[1])

    return round((60*180/pi)*2*asin(sqrt(sin((la1 - la2)/2)*sin((la1 - la2)/2)+cos(la1)*cos(la2)*sin((lo1 - lo2)/2)*sin((lo1 - lo2)/2))),6)

